package cn.wa.net.common.threads;import cn.wa.net.formbean.SysLogVo;import cn.wa.net.service.SysLogServices;import cn.wa.net.common.util.*;public class SystemLogThread implements Runnable {    private SysLogServices sysLogServices;    public SystemLogThread() {    }    public SystemLogThread(SysLogServices sysLogServices) {        this.sysLogServices = sysLogServices;    }    @Override    public void run() {        while (true) {            try {                if (sysLogServices != null) {                    if (LogQueueFactroy.getLogQueueSize() > 0) {                        SysLogVo log = LogQueueFactroy.popLog();                        sysLogServices.addSysLog(log);                    } else {                        Thread.sleep(1000);                    }                }else{                    throw new RuntimeException("SystemLogThread systemLogService is null");                }            } catch (Exception e) {                e.printStackTrace();            }        }    }}